import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

// IP адрес сервера
const String = '127.0.0.1';

function Catalog() {
  const [goods, setGoods] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 15;

  useEffect(() => {
    document.title = 'Каталог';

    // Загружаем данные с API
    fetch(`http://${String}:8000/api/goods/`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch goods');
        }
        return response.json();
      })
      .then((data) => {
        setGoods(data);
      })
      .catch((error) => {
        console.error('Error fetching goods:', error);
      });
  }, []);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = goods.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="container">
      <h1>Каталог товаров</h1>
      <div className="row">
        {currentItems.map((item) => (
          <div className="col-lg-4 col-md-6 p-4" key={item.id}>
            <div className="card border-primary rounded custom-shadow h-100">
              <img
                src={`http://${String}:8000${item.image}`}
                className="card-img-top"
                alt={item.name}
                style={{ width: '310px', height: '350px', objectFit: 'contain' }}
              />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{item.name}</h5>
                <p><strong>Характеристики:</strong> {item.description}</p>
                <div className="mt-auto d-flex justify-content-between">
                  <p><strong>Стоимость:</strong> {item.price} BYN</p>
                  <Link to={`/product/${item.id}`} className="btn btn-primary">
                    Подробнее
                  </Link>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Пагинация */}
      <nav aria-label="Page navigation example">
        <ul className="pagination justify-content-center my-4">
          <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
            <a className="page-link" href="#" onClick={() => paginate(currentPage - 1)}>
              Previous
            </a>
          </li>
          {[...Array(Math.ceil(goods.length / itemsPerPage))].map((_, index) => (
            <li
              key={index}
              className={`page-item ${currentPage === index + 1 ? 'active' : ''}`}
            >
              <a className="page-link" href="#" onClick={() => paginate(index + 1)}>
                {index + 1}
              </a>
            </li>
          ))}
          <li
            className={`page-item ${
              currentPage === Math.ceil(goods.length / itemsPerPage) ? 'disabled' : ''
            }`}
          >
            <a className="page-link" href="#" onClick={() => paginate(currentPage + 1)}>
              Next
            </a>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Catalog;
