import React, { useEffect, useState } from 'react';

function Catalog() {
  const [goods, setGoods] = useState([]);
  
  useEffect(() => {
    document.title = "Каталог";

    // Обновите URL на тот, который соответствует вашему API
    fetch('http://127.0.0.1:8000/api/goods/') 
      .then(response => {
        if (!response.ok) {
          throw new Error("Failed to fetch goods");
        }
        return response.json();
      })
      .then(data => {
        setGoods(data); 
      })
      .catch(error => {
        console.error("Error fetching goods:", error);
      });
  }, []);

  return (
    <div>
      <section>
        <div className="container">
          {/* Каталог товаров */}
          <div className="row mt-1">
            <div className="col-lg-2">{/* Placeholder for left column */}</div>
            <div className="col-lg-10">
              {/* Display goods */}
              <div className="row">
                {goods.length > 0 ? (
                  goods.map((item, index) => (
                    <div className="col-lg-4 col-md-6 p-4" key={index}>
                      <div className="card border-primary rounded custom-shadow">
                        <img src={`http://127.0.0.1:8000${item.image}`} className="card-img-top" alt={item.name} />
                        <div className="card-div">
                          <p className="card-title">{item.name}</p>
                          <p className="card-text text-truncate">{item.description}</p>
                          <div className="d-flex justify-content-between">
                            <p>
                              <strong>Стоимость:</strong> {item.price} BYN
                            </p>
                            <button className="btn btn-primary add-to-cart">Добавить в корзину</button>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>Товары не найдены.</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Catalog;
