import React, { useState, useEffect } from 'react';
import './Chat.css';
import messageIcon from './chatIcons/ChatIcon.svg';
import closeIcon from './chatIcons/CloseIcon.svg';
import sendIcon from './chatIcons/SendIcon.svg';
import searchIcon from './chatIcons/SearchIcon.svg';
import clipIcon from './chatIcons/ClipIcon.svg';
import StickerIcon from './chatIcons/StickerIcon.svg';

const Chat = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [closing, setClosing] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [file, setFile] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false); // State for emoji picker
  const [ws, setWs] = useState(null); // WebSocket state

  // Initialize WebSocket when component mounts
  useEffect(() => {
    const socket = new WebSocket('ws://localhost:8000/ws/chat/'); // Replace with your WebSocket URL
    socket.onopen = () => {
      console.log('WebSocket connected');
    };

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Message received from server:', data); // Log received data

      // If the message is "–ü—Ä–∏–≤–µ—Ç", treat it as a special message
      if (data.message && data.message === "–ü—Ä–∏–≤–µ—Ç") {
        setMessages((prev) => [
          ...prev,
          { text: data.message, type: 'received', timestamp: data.timestamp }
        ]);
      } else {
        setMessages((prev) => [...prev, data]); // Add other messages
      }
    };

    socket.onerror = (error) => {
      console.error('WebSocket Error:', error);
    };

    socket.onclose = () => {
      console.log('WebSocket connection closed');
    };

    setWs(socket);

    // Cleanup WebSocket connection when component unmounts
    return () => {
      socket.close();
    };
  }, []); // Empty dependency array to only run once on mount

  // Toggle chat window visibility
  const toggleChat = () => {
    if (isOpen) {
      setClosing(true);
      setTimeout(() => {
        setIsOpen(false);
        setClosing(false);
      }, 400);
    } else {
      setIsOpen(true);
    }
  };

  // Handle sending message
  const handleSend = () => {
    if (inputValue.trim() || file) {
      const timestamp = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

      // Send message via WebSocket if connection is open
      if (ws) {
        ws.send(
          JSON.stringify({
            message: inputValue,
            file: file ? URL.createObjectURL(file) : null, // Add file if present
            timestamp,
          })
        );
      }

      // Add the sent message to the chat UI
      setMessages([
        ...messages,
        { text: inputValue, file, type: 'sent', timestamp },
      ]);
      setInputValue(''); // Clear input field
      setFile(null); // Clear file input
    }
  };

  // Handle file input change
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  // Toggle emoji picker visibility
  const toggleEmojiPicker = () => {
    setShowEmojiPicker(!showEmojiPicker);
  };

  // Handle emoji selection
  const handleEmojiSelect = (emoji) => {
    setInputValue(inputValue + emoji); // Add emoji to input field
    setShowEmojiPicker(false); // Close emoji picker
  };

  // List of emojis
  const emojis = ['üòä', 'üòÇ', 'üò¢', 'üòç', 'üòé', 'üòú', 'üòá'];

  return (
    <div>
      {!isOpen && !closing ? (
        <div className="chat-container" onClick={toggleChat}>
          <div className="input-container">
            –ü–æ—è–≤–∏–ª–∏—Å—å –≤–æ–ø—Ä–æ—Å—ã? –ü–∏—à–∏—Ç–µ –Ω–∞–º!
            <div className="icon-container">
              <img src={messageIcon} alt="Message icon" className="icon" />
            </div>
          </div>
        </div>
      ) : (
        <div className={`chat-window ${isOpen && !closing ? 'open' : 'close'}`}>
          <div className="chat-header">
            <img src={searchIcon} alt="Search icon" className="icon" />
            –ù–∞–ø–∏—à–∏—Ç–µ –≤–∞—à–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
            <img src={closeIcon} alt="Close chat" className="icon" onClick={toggleChat} />
          </div>
          <div className="chat-body">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`message-bubble ${msg.type === 'sent' ? 'sent' : 'received'}`}
              >
                {msg.text && <p>{msg.text}</p>}
                {msg.file && (
                  <div className="file-attachment">
                    üìé <a href={msg.file} target="_blank" rel="noopener noreferrer">{msg.file}</a>
                  </div>
                )}
                {msg.timestamp && <span className="timestamp">{msg.timestamp}</span>}
              </div>
            ))}
          </div>

          <div className="chat-input">
            <label htmlFor="file-input">
              <img src={clipIcon} alt="Attach icon" className="clip-icon" />
            </label>
            <input
              id="file-input"
              type="file"
              onChange={handleFileChange}
              style={{ display: 'none' }}
            />
            <input
              type="text"
              placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ"
              className="input-with-emoji"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
            />
            <button className="emoji-button" onClick={toggleEmojiPicker}>
              <img src={StickerIcon} alt="Emoji icon" />
            </button>
            <button className="send-button" onClick={handleSend}>
              <img src={sendIcon} alt="Send icon" />
            </button>

            {showEmojiPicker && (
              <div className="emoji-picker">
                {emojis.map((emoji, index) => (
                  <button
                    key={index}
                    className="emoji"
                    onClick={() => handleEmojiSelect(emoji)}
                  >
                    {emoji}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
