import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const String = '127.0.0.1';

function Product() {
  const { name } = useParams();
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!name) {
      console.error('Отсутствует параметр имени продукта');
      return;
    }

    fetch('http://' + String + ':8000/api/goods/' + name)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Товар не найден');
        }
        return response.json();
      })
      .then((data) => {
        setProduct(data);
      })
      .catch((error) => {
        console.error('Ошибка при загрузке товара:', error);
        alert('Товар не найден. Попробуйте еще раз.');
      });
  }, [name]);

  const handleAddToCart = () => {
    console.log(`Товар ${product.name} добавлен в корзину!`);
  };

  return (
    <>
      {/* Header with Navigation */}
      <header>
        <nav className="navbar navbar-expand-lg bg-dark" data-bs-theme="dark">
          <div className="container">
            <a className="navbar-brand" href="/">Home</a>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarSupportedContent"
              aria-controls="navbarSupportedContent"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
              <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">
                  <a className="nav-link text-white" href="/cart">Корзина</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link text-white" href="/login">Войти</a>
                </li>
              </ul>
              <form className="d-flex" role="search">
                <input
                  className="form-control me-2"
                  type="search"
                  placeholder="Search"
                  aria-label="Search"
                />
                <button className="btn btn-outline-success text-white" type="submit">
                  Поиск
                </button>
              </form>
            </div>
          </div>
        </nav>
      </header>

      {/* Main Product Section */}
      <section className="container mt-5">
        {product ? (
          <div className="row">
            <div className="col-md-6">
              <img
                src={`http://${String}:8000${product.image}`}
                alt={product.name}
                className="img-fluid"
                style={{ objectFit: 'contain' }}
              />
            </div>
            <div className="col-md-6">
              <h2>{product.name}</h2>
              <p><strong>Описание:</strong> {product.description}</p>
              <p><strong>Цена:</strong> {product.price} BYN</p>
              <button className="btn btn-primary" onClick={handleAddToCart}>
                Добавить в корзину
              </button>
              <button className="btn btn-secondary ml-3" onClick={() => navigate(-1)}>
                Назад
              </button>
            </div>
          </div>
        ) : (
          <div className="container text-center">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Загрузка...</span>
            </div>
          </div>
        )}
      </section>

      {/* Footer */}
      <footer className="bg-dark text-center text-white py-3 mt-5">
        <p>&copy; 2024 Your Store Name</p>
      </footer>
    </>
  );
}

export default Product;
