import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const String = '127.0.0.1';

function Product() {
  const { name } = useParams(); 
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://${String}:8000/api/goods/${encodeURIComponent(name)}/`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Товар не найден');
        }
        return response.json();
      })
      .then((data) => {
        setProduct(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, [name]);

  const handleAddToCart = () => {
    if (product) {
      // Пример отправки данных на сервер с использованием POST
      fetch('http://127.0.0.1:8000/api/cart/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          productId: product.id,  // передаем ID товара
          quantity: 1,  // количество товара
        }),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Не удалось добавить товар в корзину');
          }
          return response.json();
        })
        .then((data) => {
          console.log('Товар добавлен в корзину:', data);
          alert('Товар успешно добавлен в корзину!');
        })
        .catch((error) => {
          console.error('Ошибка при добавлении товара в корзину:', error);
          alert('Ошибка при добавлении товара в корзину. Попробуйте снова.');
        });
    }
  };

  if (loading) {
    return (
      <div className="container text-center">
        <div style={{ padding: '35px', borderRadius: '15px' }}></div>
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container text-center">
        <p>{error}</p>
        <button className="btn btn-secondary" onClick={() => navigate(-1)}>
          Назад
        </button>
      </div>
    );
  }

  return product ? (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-6">
          <img
            src={`http://${String}:8000${product.image}`}
            alt={product.name}
            className="img-fluid"
            style={{ objectFit: 'contain' }}
          />
        </div>
        <div className="col-md-6">
          <h2>{product.name}</h2>
          <p><strong>Описание:</strong> {product.description}</p>
          <p><strong>Цена:</strong> {product.price} BYN</p>
          <button className="btn btn-primary" onClick={handleAddToCart}>
            Добавить в корзину
          </button>
          <button className="btn btn-secondary ml-3" onClick={() => navigate(-1)}>
            Назад
          </button>
        </div>
      </div>
    </div>
  ) : null;
}

export default Product;
