import React, { useState, useEffect } from 'react';
import './Chat.css';
import './ChatAuth.css'
import './Error.css'
import axios from 'axios'
import messageIcon from './chatIcons/ChatIcon.svg';
import closeIcon from './chatIcons/CloseIcon.svg';
import backArrow from './chatIcons/BackArrow.png'
import sendIcon from './chatIcons/SendIcon.svg';
import stickerIcon from './chatIcons/StickerIcon.svg';

const Chat = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const [file, setFile] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [ws, setWs] = useState(null);

  const [error, setError] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [fromUser, setFromUser] = useState('');
  const [toUser, setToUser] = useState('');
  const [messages, setMessages] = useState([]);
  const [password, setPassword] = useState('');

  const [chats, setChats] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  // Отправка сообщения
  const handleSend = () => {
    if (inputValue.trim() || file) {
      const timestamp = new Date().toLocaleTimeString([], {hour: '2-digit', minute: '2-digit'});
      const messageData = {
        text: inputValue,
        file: file ? URL.createObjectURL(file) : null,
        type: 'sended',  // Тип сообщения 'sended' для отправленного
        timestamp
      };

      // Отправка сообщения через WebSocket
      ws.send(JSON.stringify(messageData));

      // Добавляем сообщение в локальное состояние
      setMessages((prevMessages) => [...prevMessages, messageData]);
      setInputValue('');
      setFile(null);
    }
  };

  // Обработка получения сообщений через WebSocket
  useEffect(() => {
    if (ws) {
      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        console.log("Message received:", data);

        // Добавляем полученное сообщение в локальное состояние с типом 'received'
        setMessages((prevMessages) => [
          ...prevMessages,
          { ...data, type: 'received' }
        ]);
      };
    }
  }, [ws]);

  // Логика для логина и регистрации не изменяется
  const handleLogin = async () => {
    if (fromUser && password) {
      try {
        let response = await axios.post('http://localhost:8000/chat/login/', { fromUser, password });
        if (response.data.success) {
          setIsAuthenticated(true);
        } else {
          setError('Неверный логин или пароль');
        }
      } catch (error) {
        setError('Ошибка при авторизации');
      }
    } else {
      setError('Введите корректные данные для входа.');
    }
  };

  const handleClickOnChat = async (toUser) => {
    const response = await axios.post('http://localhost:8000/chat/load/', { fromUser, toUser });
    console.log(response.data);
    setChats(response.data);

    // Подключение к WebSocket
    const socket = new WebSocket(`ws://localhost:8000/ws/chats/${fromUser}/${toUser}/`);

    const waitForConnection = new Promise((resolve, reject) => {
      socket.onopen = () => resolve(socket);
      socket.onerror = (error) => reject(error);
    });

    waitForConnection
      .then((connectedSocket) => {
        setWs(connectedSocket);
      })
      .catch((error) => {
        console.error("Failed to establish WebSocket connection:", error);
      });

    socket.onclose = () => {
      console.log('WebSocket connection closed');
    };
  };

  // UI для аутентификации
  if (!isAuthenticated) {
    return (
      <div className="auth-container">
        <div className="auth-box">
          <h2>{isRegistering ? 'Регистрация' : 'Вход'}</h2>
          <input
            type="text"
            placeholder="Логин"
            value={fromUser}
            onChange={(e) => setFromUser(e.target.value)}
            className="auth-input"
          />
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="auth-input"
          />
          <button onClick={isRegistering ? handleRegister : handleLogin} className="auth-button">
            {isRegistering ? 'Зарегистрироваться' : 'Войти'}
          </button>
          <button onClick={() => setIsRegistering(!isRegistering)} className="toggle-button">
            {isRegistering ? 'Уже есть аккаунт? Войти' : 'Нет аккаунта? Зарегистрироваться'}
          </button>

          {error && (
            <div className="auth-error">
              <span>{error}</span>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div>
      {!isOpen ? (
        <div className="chat-container" onClick={() => setIsOpen(true)}>
          <div className="input-container">
            Появились вопросы? Пишите нам!
            <div className="icon-container">
              <img src={messageIcon} alt="Message icon" className="icon" />
            </div>
          </div>
        </div>
      ) : (
        <div className={`chat-window ${isOpen ? 'open' : 'close'}`}>
          <div className="chat-header">
            <img
              src={backArrow}
              alt="Назад"
              className="icon"
              onClick={() => {
                ws.close();
                setWs(null);
                setToUser(null);
              }}
            />
            <span>Чат с {toUser}</span>
            <img
              src={closeIcon}
              alt="Close chat"
              className="icon"
              onClick={() => setIsOpen(false)}
            />
          </div>

          <div className="chat-body">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`message-bubble ${msg.type === 'sended' ? 'sent' : 'received'}`}
              >
                <p>{msg.text}</p>
                <span className="timestamp">{msg.timestamp}</span>
              </div>
            ))}
          </div>

          <div className="chat-input">
            <input
              type="text"
              placeholder="Введите сообщение"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              className="input-with-emoji"
            />
            <img
              src={stickerIcon}
              alt="Стикеры"
              className="emoji-button"
              onClick={() => setShowEmojiPicker(!showEmojiPicker)}
            />
            <img
              src={sendIcon}
              alt="Отправить"
              className="send-button"
              onClick={handleSend}
            />
            {showEmojiPicker && (
              <div className="emoji-picker">
                {['😊', '😂', '😍', '😎'].map((emoji, index) => (
                  <button
                    key={index}
                    onClick={() => setInputValue(inputValue + emoji)}
                  >
                    {emoji}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
