import React, { useState } from 'react';
import './Chat.css';
import messageIcon from './ChatIcon.svg';
import closeIcon from './CloseIcon.svg';
import sendIcon from './SendIcon.svg';
import searchIcon from './SearchIcon.svg';
import stickerIcon from './StickerIcon.svg';

const Chat = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [closing, setClosing] = useState(false);
  const [message, setMessage] = useState("");

  const toggleChat = () => {
    if (isOpen) {
      setClosing(true);
      setTimeout(() => {
        setIsOpen(false);
        setClosing(false);
      }, 400);
    } else {
      setIsOpen(true);
    }
  };

  const handleEmojiClick = () => {
    // Here, you could open an emoji picker or add a default emoji to the input
    setMessage(message + "üòä"); // Adds a smile emoji to the input
  };

  return (
    <div>
      {!isOpen && !closing ? (
        <div className="chat-container" onClick={toggleChat}>
          <div className="input-container">
            –ü–æ—è–≤–∏–ª–∏—Å—å –≤–æ–ø—Ä–æ—Å—ã? –ü–∏—à–∏—Ç–µ –Ω–∞–º!
            <div className="icon-container">
              <img src={messageIcon} alt="Message icon" className="icon" />
            </div>
          </div>
        </div>
      ) : (
        <div className={`chat-window ${isOpen && !closing ? 'open' : 'close'}`}>
          <div className="chat-header">
            <img src={searchIcon} alt="Search icon" className="icon" />
            –ù–∞–ø–∏—à–∏—Ç–µ –≤–∞—à–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
            <img src={closeIcon} alt="Close chat" className="icon" onClick={toggleChat} />
          </div>
          <div className="chat-body">
            {/* Messages will go here */}
          </div>
          <div className="chat-input">
            <button onClick={handleEmojiClick} className="emoji-button">
              <img src={stickerIcon} alt="Emoji icon" />
            </button>
            <input 
              type="text" 
              placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ" 
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button className="send-button">
              <img src={sendIcon} alt="Send icon" />
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
