import React, { useState, useEffect } from 'react';
import './Chat.css';
import './ChatAuth.css'
import './Error.css'
import axios from 'axios'
import messageIcon from './chatIcons/ChatIcon.svg';
import closeIcon from './chatIcons/CloseIcon.svg';
import sendIcon from './chatIcons/SendIcon.svg';
import searchIcon from './chatIcons/SearchIcon.svg';
import clipIcon from './chatIcons/ClipIcon.svg';
import StickerIcon from './chatIcons/StickerIcon.svg';

const Chat = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [closing, setClosing] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [file, setFile] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [ws, setWs] = useState(null);

  const [error, setError] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Authentication state
  const [isRegistering, setIsRegistering] = useState(false); // Registration state
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [chats, setChats] = useState([]); // Все чаты
  const [searchQuery, setSearchQuery] = useState(""); // Запрос поиска
  const [selectedChat, setSelectedChat] = useState(null); // Текущий выбранный чат


  useEffect(() => {
    console.log("using useEffect")
    console.log(new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit'}));
    const socket = new WebSocket(`ws://localhost:8000/ws/chats/${username}/`);
    socket.onopen = () => console.log('WebSocket connected');
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log(data)
      setMessages((prev) => [...prev, data]);
      // setMessages([data])
    };
    socket.onerror = (error) => console.error('WebSocket Error:', error);
    socket.onclose = () => console.log('WebSocket connection closed');
    setWs(socket);

    return () => {
      socket.close();
    };
  }, [username]);

  const handleSend = () => {
    if (inputValue.trim() || file) {
      const timestamp = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
      if (ws) {
        ws.send(JSON.stringify({ text: inputValue, file: file ? URL.createObjectURL(file) : null, timestamp }));
      }
      setMessages([...messages, { text: inputValue, file, type: 'sent', timestamp }]);
      setInputValue('');
      setFile(null);
    }
  };

  const handleLogin = async () => {
  if (username && password) {
    setIsAuthenticated(true);
    // try {
    //   const response = await axios.post('http://localhost:8000/chat/login/', { username, password });
    //   if (response.data.success) {
    //     setIsAuthenticated(true);
    //   } else {
    //     setError('Неверный логин или пароль');
    //   }
    // } catch (error) {
    //   setError('Ошибка при авторизации');
    // }
  } else {
    setError('Введите корректные данные для входа.');
  }
};


  const handleRegister = () => {
    // Mock registration logic; replace with API call if needed
    if (username && password) {
      alert('Пользователь зарегистрирован!');
      setIsRegistering(false);
    } else {
      alert('Введите корректные данные для регистрации.');
    }
  };

  if (!isAuthenticated) {
  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2>{isRegistering ? 'Регистрация' : 'Вход'}</h2>
        <input
          type="text"
          placeholder="Логин"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="auth-input"
        />
        <input
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="auth-input"
        />
        <button onClick={isRegistering ? handleRegister : handleLogin} className="auth-button">
          {isRegistering ? 'Зарегистрироваться' : 'Войти'}
        </button>
        <button onClick={() => setIsRegistering(!isRegistering)} className="toggle-button">
          {isRegistering ? 'Уже есть аккаунт? Войти' : 'Нет аккаунта? Зарегистрироваться'}
        </button>

        {/* Отображение ошибки */}
        {error && (
  <div className="auth-error">
    <img src="/path/to/error-icon.svg" alt="Error" className="error-icon" />
    <span>{error}</span>
  </div>
)}
      </div>
    </div>
  );
}



  return (
  <div>
    {!isOpen && !closing ? (
      <div className="chat-container" onClick={() => setIsOpen(true)}>
        <div className="input-container">
          Появились вопросы? Пишите нам!
          <div className="icon-container">
            <img src={messageIcon} alt="Message icon" className="icon" />
          </div>
        </div>
      </div>
    ) : (
      <div className={`chat-window ${isOpen && !closing ? 'open' : 'close'}`}>
        <div className="chat-header">
          <img src={searchIcon} alt="Search icon" className="icon" />
          Напишите ваше сообщение
          <img src={closeIcon} alt="Close chat" className="icon" onClick={() => setIsOpen(false)} />
        </div>

        {/* Поле поиска */}
        <div className="search-field">
          <input
            type="text"
            placeholder="Поиск чатов..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
        </div>

        {/* Список чатов */}
        <div className="chat-list">
          {chats
            .filter((chat) =>
              chat.username.toLowerCase().includes(searchQuery.toLowerCase())
            )
            .map((chat, index) => (
              <div
                key={index}
                className={`chat-item ${selectedChat === chat.username ? 'selected' : ''}`}
                onClick={() => setSelectedChat(chat.username)}
              >
                <span>{chat.username}</span>
              </div>
            ))}
        </div>

        {/* Переписка с выбранным собеседником */}
        {selectedChat && (
          <div className="chat-body">
            {messages
              .filter((msg) => msg.username === selectedChat)
              .map((msg, index) => (
                <div
                  key={index}
                  className={`message-bubble ${msg.type === 'sent' ? 'sent' : 'received'}`}
                >
                  {msg.text && <p>{msg.text}</p>}
                  {msg.file && (
                    <div className="file-attachment">
                      📎 <a href={msg.file} target="_blank" rel="noopener noreferrer">{msg.file}</a>
                    </div>
                  )}
                  {msg.timestamp && <span className="timestamp">{msg.timestamp}</span>}
                </div>
              ))}
          </div>
        )}

        {/* Поле ввода сообщения */}
        <div className="chat-input">
          <label htmlFor="file-input">
            <img src={clipIcon} alt="Attach icon" className="clip-icon" />
          </label>
          <input
            id="file-input"
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            style={{ display: 'none' }}
          />
          <input
            type="text"
            placeholder="Введите сообщение"
            className="input-with-emoji"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
          />
          <button className="emoji-button" onClick={() => setShowEmojiPicker(!showEmojiPicker)}>
            <img src={StickerIcon} alt="Emoji icon" />
          </button>
          <button className="send-button" onClick={handleSend}>
            <img src={sendIcon} alt="Send icon" />
          </button>

          {showEmojiPicker && (
            <div className="emoji-picker">
              {['😊', '😂', '😢', '😍', '😎', '😜', '😇'].map((emoji, index) => (
                <button key={index} onClick={() => setInputValue(inputValue + emoji)}>
                  {emoji}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>
    )}
  </div>
);
};

export default Chat;
