import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const String = '127.0.0.1';

function Product() {
  const { name } = useParams(); // Получаем параметр из URL
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Если name пустой, не выполняем запрос
    if (!name) {
      console.error('Отсутствует параметр имени продукта');
      return;
    }

    const encodedName = encodeURIComponent(name); // Кодируем имя продукта для URL

    fetch(`http://${String}:8000/api/goods/${encodedName}/`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Товар не найден');
        }
        return response.json();
      })
      .then((data) => {
        setProduct(data); // Устанавливаем полученные данные
      })
      .catch((error) => {
        console.error('Ошибка при загрузке товара:', error);
        alert('Товар не найден. Попробуйте еще раз.');
      });
  }, [name]); // Эффект запускается при изменении параметра name

  const handleAddToCart = () => {
    // Логика добавления товара в корзину
    console.log(`Товар ${product.name} добавлен в корзину!`);
  };

  return product ? (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-6">
          <img
            src={`http://${String}:8000${product.image}`}
            alt={product.name}
            className="img-fluid"
            style={{ objectFit: 'contain' }}
          />
        </div>
        <div className="col-md-6">
          <h2>{product.name}</h2>
          <p><strong>Описание:</strong> {product.description}</p>
          <p><strong>Цена:</strong> {product.price} BYN</p>
          <button className="btn btn-primary" onClick={handleAddToCart}>
            Добавить в корзину
          </button>
          <button className="btn btn-secondary ml-3" onClick={() => navigate(-1)}>
            Назад
          </button>
        </div>
      </div>
    </div>
  ) : (
    <div className="container text-center">
      <div style={{ padding: '35px', borderRadius: '15px' }}></div>
      <div className="spinner-border" role="status">
        <span className="visually-hidden">Загрузка...</span>
      </div>
    </div>
  );
}

export default Product;
