import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function Product() {
  const { id } = useParams(); // Получаем id из URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Загрузка данных о товаре по id
    fetch(`http://your-api-url:8000/products/${id}`)
      .then(response => response.json())
      .then(data => setProduct(data))
      .catch(error => console.error(error));
  }, [id]);

  if (!product) {
    return <p>Загрузка...</p>;
  }

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-6">
          <img
            src={`http://your-api-url:8000${product.image}`}
            className="img-fluid"
            alt={product.name}
          />
        </div>
        <div className="col-md-6">
          <h1>{product.name}</h1>
          <p><strong>Описание:</strong> {product.description}</p>
          <p><strong>Стоимость:</strong> {product.price} BYN</p>
          <button className="btn btn-dark">Добавить в корзину</button>
        </div>
      </div>
    </div>
  );
}

export default ProductPage;
