import React, { useState } from 'react';
import './Chat.css';
import messageIcon from './ChatIcon.svg';

const Chat = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="chat-container" onClick={toggleChat}>
      <div className="input-container">
        {isOpen ? (
          <div>
            <p>Появились вопросы? Пишите нам!</p>
            {/* Chat messages or input area can go here */}
          </div>
        ) : (
          <p>Появились вопросы? Пишите нам!</p>
        )}
        <div className="icon-container">
          <img src={messageIcon} alt="Message icon" className="icon" />
        </div>
      </div>
      {isOpen && (
        <div className="chat-messages">
          {/* Example chat messages */}
          <p>Сообщение 1</p>
          <p>Сообщение 2</p>
          {/* You can add more chat functionality here */}
        </div>
      )}
    </div>
  );
};

export default Chat;
