import React, { useEffect } from 'react';
import { useTranslation } from 'react-i18next';

function About() {
  const { t } = useTranslation();

  // Developer names for both Russian and Latin versions
  const developerNames = {
    ru: ["Артём", "Лев", "Алина", "Алексей"],
    en: ["Artem", "Lev", "Alina", "Alexey"]
  };

  useEffect(() => {
    document.title = t('aboutTitle'); // Localized document title
  }, [t]);

  return (
    <div>
      <div style={{ padding: '15px', borderRadius: '15px' }}></div>
      <section>
        <div className="container">
          <div className="row mt-1">
            <div className="col-lg-12">
              <div className="text-background">
                <h3 className="text-center">{t('developerInfo')}</h3> {/* Localized header */}
                <div className="d-flex justify-content-center flex-wrap">
                  {["благодать.jpg", "лев.jpg", "ра.jpg", "лёха.jpg"].map((img, index) => (
                    <div className="p-4" key={index}>
                      <a className="card-div">
                        <img src={`../deps/images/developers/${img}`} className="card-img" alt={t('developerImageAlt')} />
                        <h5 className="card-title text-center">{developerNames[t('language')][index]}</h5> {/* Localized names */}
                      </a>
                    </div>
                  ))}
                </div>
                <h3 className="text-center">{t('simpleWish')}</h3> {/* Localized footer text */}
              </div>
            </div>
          </div>
        </div>
      </section>
      <div style={{ padding: '50px', borderRadius: '15px' }}></div>
    </div>
  );
}

export default About;
